import { useEffect, useState } from 'react';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart, registerables } from 'chart.js';
import { useAuth } from '../context/AuthContext';

Chart.register(...registerables);

export default function Dashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    patients: 0,
    staff: 0,
    revenue: 0,
    expenses: 0
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [patientsRes, staffRes, financeRes] = await Promise.all([
          axios.get('/api/patients'),
          axios.get('/api/staff'),
          axios.get('/api/finance/summary')
        ]);
        
        setStats({
          patients: patientsRes.data.length,
          staff: staffRes.data.length,
          revenue: financeRes.data.revenue,
          expenses: financeRes.data.expenses
        });
      } catch (err) {
        console.error(err);
      }
    };
    
    fetchStats();
  }, []);

  const patientStatusData = {
    labels: ['Admitted', 'Discharged', 'Critical'],
    datasets: [
      {
        data: [65, 25, 10],
        backgroundColor: ['#3B82F6', '#10B981', '#EF4444']
      }
    ]
  };

  const financeData = {
    labels: ['Revenue', 'Expenses'],
    datasets: [
      {
        label: 'Amount',
        data: [stats.revenue, stats.expenses],
        backgroundColor: ['#10B981', '#EF4444']
      }
    ]
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Hospital Dashboard</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-gray-500">Total Patients</h3>
          <p className="text-3xl font-bold">{stats.patients}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-gray-500">Total Staff</h3>
          <p className="text-3xl font-bold">{stats.staff}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-gray-500">Total Revenue</h3>
          <p className="text-3xl font-bold">${stats.revenue}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-gray-500">Total Expenses</h3>
          <p className="text-3xl font-bold">${stats.expenses}</p>
        </div>
      </div>
      
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">Patient Status</h3>
          <Pie data={patientStatusData} />
        </div>
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-semibold mb-4">Financial Overview</h3>
          <Bar data={financeData} />
        </div>
      </div>
    </div>
  );
}
